import React from "react";

const PopUp = () => {

    const [close, setclose] = useState(false);
    const handleclose = () => {
        setclose(true)
    }

    return (
        <div className="modal">
            <div className="modal_content">
                <div class="popupCloseButton" onClick={handleclose}> &times;</div>
                <form>
                    <h3>Register!</h3>
                    <label>
                        Name:
                    <input type="text" name="name" />
                    </label>
                    <br />
                </form>
            </div>
           close? <PopUp /> :null
        </div>
    )
};

export default PopUp;
