import React, { useState, createContext } from "react";
export const Context = createContext();

export const Roomprovider = props => {

    const [rooms, setrooms] = useState([
        {
            ...rooms,
            id: 1,
            name: "ROOM1",
            foto: "PHOTO1"
        },
        {
            id: 2,
            name: "ROOM2",
            foto: 'PHOTO2'
        }
    ]);

    return (
        <Context.Provider value={[rooms, setrooms]}>
            {props.children}
        </Context.Provider>
    );
}
