import React, { useState } from "react";

const PopUp = () => {

    const handleclose = () => {
        setvisible(false)
    }

    return (
        <div className="modal">
            <div className="modal_content">
                <div class="popupCloseButton" onClick={handleclose}>&times;</div>
                <form>
                    <h3>Register!</h3>
                    <label>
                        Name:
                    <input type="text" name="name" />
                    </label>
                    <br />
                </form>
            </div>
        </div>
    )
};

export default PopUp;
