import headerhero from '../assets/g/images/headerhero.png';
import logo from '../assets/g/images/logo.svg';
import Login from './Login.js';
import React from 'react';
import PopUp from './popup';
import { useState, useContext, useEffect } from 'react';
import { Context } from "./context";
import Chat from "./chatroom";
import Button from '@material-ui/core/Button';
import Modal from '@material-ui/core/Modal';
import { makeStyles } from '@material-ui/core/styles';
import SimpleModal from '@material-ui/core/Modal';
import { TextField, MenuItem, TextareaAutosize } from '@material-ui/core';






function rand() {
    return Math.round(Math.random() * 20) - 10;
}

function getModalStyle() {
    const top = 50 + rand();
    const left = 50 + rand();

    return {
        top: `${top}%`,
        left: `${left}%`,
        transform: `translate(-${top}%, -${left}%)`,
    };
}

const useStyles = makeStyles((theme) => ({
    paper: {
        position: 'absolute',
        width: 400,
        backgroundColor: theme.palette.background.paper,
        border: '2px solid #000',
        boxShadow: theme.shadows[5],
        padding: theme.spacing(2, 4, 3),
    },
}));




export default function Entete() {
    const [roomtopic, setroomtopic] = useState('');
    const [description, setdescription] = useState('');
    const [currency, setCurrency] = React.useState('');
    const [submit, setsubmit] = useState(false);

    const currencies = [
        {
            value: '1',
            label: '1',
        },
        {
            value: '2',
            label: '2',
        },
        {
            value: '3',
            label: '3',
        },
        {
            value: '4',
            label: '4',
        },
    ];

    const [rooms, setrooms] = useContext(Context);



    const handlechange = (e) => {
        setroomtopic(e.target.value);
    }
    const handlechange1 = (e) => {
        setdescription(e.target.value);
    }

    const Submit = (e) => {
        console.log("hi i'm inside submit")
        setsubmit(true);
        e.preventDefault();

        //Add to the previous rooms a new instance 
        setrooms(prevrooms => [...prevrooms, { name: roomtopic, description: description }]);

        var newWindow = window.open('/chatroom');
        newWindow.my_special_setting = roomtopic;
        console.log(newWindow.my_special_setting)


    }


    const classes = useStyles();
    // getModalStyle is not a pure function, we roll the style only on the first render
    const [modalStyle] = React.useState(getModalStyle);
    const [open, setOpen] = React.useState(false);

    const handleOpen = () => {
        setOpen(true);
    };

    const handleClose = () => {
        setOpen(false);
    };

    const body = (
        <div style={modalStyle} className={classes.paper}>

                <form className="modal">
                    {console.log("hi i'm inside Popup")}

                    <div className="room-info">
                        <div className="container-topic-participants">
                            <TextField id="standard-helperText" label="Topic"
                                value={roomtopic}
                                onChange={handlechange}
                                color="secondary" />
