import React, { useState } from 'react';
import ReactDOM from 'react-dom';
import GoogleLogin from 'react-google-login';
import Button from '@material-ui/core/Button';
import { GoogleLogout } from 'react-google-login';
import { useGoogleLogin } from 'react-google-login'


/* global gapi */

//LogIn using google Autho

function Login() {

      const [Authentificated, setAuthentificated] = useState(false);

      const responseGoogle = response => {
            const jwtoken = response.tokenId;
            alert(jwtoken)
            console.log(jwtoken)
      };

      function failure() {
            alert("sorry couldn't log in ")
      }

      function signOut() {
            var auth2 = gapi.auth2.getAuthInstance();
            auth2.signOut().then(function () {
                  alert("sorry! you are out")
            });
      }

      function logoutfailure() {
            alert("sorry we couldn't log out, try again later ")
      }


      // SEND TOKEN TO THE BACK-END ( EXPRESS)

      /* TO DO 

       Check Logged In  ComponentDidMount() does
       most of our heavy lifting. If the user is
       not logged in, we instantly redirect them
       to the login page. Otherwise, we make an 
       attempt to get the information that is
       stored in the token and save it in the 
       state.
      ------------------------------------------
      ------------------------------------------
      ------------------------------------------
      ------------------------------------------
      -----------------------------------------
      */


      return (
            <div className="Login-button">
                  <Button >
                        <GoogleLogin
                              clientId="740831316427-m1qet5irfugkeva79brhb6s6nim8ds55.apps.googleusercontent.com"
                              buttonText="Login"
                              onSuccess={responseGoogle}
                              onFailure={failure}
                              cookiePolicy={'single_host_origin'}
                              isSignedIn={true}
                        />

                  </Button>

                  <Button>
                        <GoogleLogout
                              clientId="740831316427-m1qet5irfugkeva79brhb6s6nim8ds55.apps.googleusercontent.com"
                              buttonText="Logout"
                              onLogoutSuccess={signOut}
                              onFailure={logoutfailure}
                        />
                  </Button>

            </div>

      )
}

export default Login;
