import React, { useState } from "react";
import Corps from "./Corps"
const PopUp = ({ }) => {
    const [pop, setpop] = useState(true);

    const handleclose = () => {
        setpop(false);
    }

    return (

        <div className="modal">
            <div className="modal_content">
                <div class="popupCloseButton" onClick={handleclose}>&times;</div>
                <form>
                    <h3>Register!</h3>
                    <label>
                        Name:
                    <input type="text" name="name" />
                    </label>
                    <br />
                    {pop ? <Corps name="false" /> : null}
                </form>
            </div>
        </div>
    )
};

export default PopUp;
