import { Roomprovider } from "./components/context";
import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import Entete from "./components/entete.js";
import Corps from "./components/Corps.js";
import Footer from "./components/Footer.js";
import Chat from "./components/chatroom.js";
import "./App.css"


function App() {

  const [apiResponse, setapiResponse] = useState('');
  const [loading, setLoading] = useState(true);


  //Connect React && Node.JS

  async function callAPI() {
    const response = await fetch("http://localhost:9000/testAPI");
    const json = await response.json();
    setapiResponse(json);
    setLoading(false);
  }

  useEffect(() => {
    callAPI();
  }, []);

  return (

    < Roomprovider>

      <div className="grid">
        <div className="entete" >
          {apiResponse}
          <Entete />
        </div>

        <div className="corps">

          <Corps />

        </div>

        <div className="footer">
          <Footer />
        </div>
      </div>
    </Roomprovider>

  );
}

export default App;
