import logo from './logo.png';
import Login from './Login.js';
import Logo from './Logo.js';
import Register from './Register.js'
import Room from './room.js'
import { useState } from 'react';



const Corps = ()=> {
//create state and initiate it 
const [isRoomShown, setRoomShown] = useState(false);
const handlePreview=(e)=> { //event handler 
                    e.preventDefault();
                    setRoomShown(true); //display the room
                           }
return (
             <div>
                 <div className = "rooms-section-title"> #Live now! 
                  <div className="room-new-button"> 
                         <button onClick={handlePreview}> Create Room </button> 
                  </div>
                 </div> 
                            {isRoomShown ? <Room name='selma dahmani' photo = 'Hey sup ! im a smart idiot'/> : null}
                           </div>

       )
                     };

export default Corps;
