import React from "react";
import Corps from "./Corps";
import { useState, useContext } from 'react';
import { Context } from "./context";
import { TextField, Button, MenuItem, TextareaAutosize, Box } from '@material-ui/core';


function Chat({ name, message }) {
       const [roomtopic, setroomtopic] = useState('');
       const [description, setdescription] = useState('');
       const [currency, setCurrency] = React.useState('');
       const currencies = [
              {
                     value: '1',
                     label: '1',
              },
              {
                     value: '2',
                     label: '2',
              },
              {
                     value: '3',
                     label: '3',
              },
              {
                     value: '4',
                     label: '4',
              },
       ];

       const [rooms, setrooms] = useContext(Context);
       const handlechange = (e) => {
              setroomtopic(e.target.value);
       }
       const handlechange1 = (e) => {
              setdescription(e.target.value);
       }

       const Submit = (e) => {
              e.preventDefault();
              //Add to the previous rooms a new instance 
              setrooms(prevrooms => [...prevrooms, { name: roomtopic, foto: description }]);
       }

       // <div className="popupCloseButton" onClick={valeur}> &times;</div>
       return (
              <Box component="span" m={1}>
                     <form className="modal">
                            <div className="room-info">
                                   <div className="container-topic-participants">
                                          <TextField id="standard-helperText" label="Topic "
                                                 value={roomtopic}
                                                 onChange={handlechange}
                                                 color="secondary" />

                                          <TextField
                                                 id="standard-select-currency"
                                                 select
                                                 label="Participant Number"
                                                 value={currency}
                                                 helperText="Define the max number of participants"
                                          >
                                                 {currencies.map((option) => (
                                                        <MenuItem key={option.value} value={option.value}>
                                                               {option.label}
                                                        </MenuItem>
                                                 ))}
                                          </TextField>
                                          <br></br> <br></br>
                                          <div className="submit"> <Button type="submit" onClick={Submit} variant="contained" color="secondary"> SUBMIT </Button></div>
                                   </div>

                                   <div className="container-room-description">
                                          <TextareaAutosize aria-label="minimum height"
                                                 rowsMin={5} placeholder="Description"
                                                 value={description}
                                                 onChange={handlechange1}
                                          />
                                          <div className="close"> <Button type="close" variant="contained" color="secondary"> Close </Button></div>

                                   </div>
                            </div>

                     </form>
              </Box>
       );
}
export default Chat;
