import React, { useEffect } from 'react';
import PopUp from './popup'
import { useState } from 'react';
import { Link } from "react-router-dom";
import { UserProvider } from "./UserContext"


const Corps = () => {

       const [user, setUser] = useState([]);
       //By default, the rooms list is empty


       const [rooms, setrooms] = useState([]);
       //Popup state

       const [visible, setvisible] = useState(false);
       //handle close for popup, ist past as a prop to popup

       const handleclose = () => {
              setvisible(false)
       }
       const handlesubmit = (userName) => {
              const data = [{ "name": userName }, { "name": "test2" }];
       }

       //SYNTAXE USE  event.target.name, event.target.value;
       //Onlick callback function: add a new room to the array
       const addroom = () => {
              setvisible(true)
              setrooms([...rooms,
              {
                     id: 1,
                     name: 'selmadahmani',
                     foto: 'this is my photo'
              }
              ]);
       };


       //Display the list of Live rooms
       const roomslist = rooms.map(name => {
              return (
                     <div className="room-info">
                            <div className="room-name"> {name.name} </div>
                            <div className="room-photo"> {name.foto} </div>
                            <div className="room-join-button" > <Link className="room-join-button" to="./chatroom" target="_blank"> Join  </Link> </div>
                     </div>
              )
       })

       return (

              <div>
                     <div className="rooms-section-title"> #Live now!
                     <div className="room-new-button">
                                   {/* onclick state changes(new room added), which means that the rooms will be rendered once again */}
                                   <button onClick={addroom}>  Create Room  </button>
                            </div>
                     </div>


                     <UserProvider value={rooms}>

                            {visible ? <PopUp valeur={handleclose} submit={handlesubmit} /> : null} {/* if visible render the popup if not don't render anything */}

                     </UserProvider>

                     <div className="rooms-container">
                            {roomslist} {/* Map through the rooms list && display them */}
                     </div>
              </div>
       )
};

export default Corps;
