       const [rooms, setrooms] = useState([]);

       const addroom = () => {
         setrooms([...rooms,
         {   id:1,
             name: 'selmadahmani',
             foto: 'this is my photo'
           }        
         ]);

       };

       const roomslist = rooms.map (name => {
              return ( 

                      <div>
                       <div className="room-name"> {name.name} </div>
                       <div className="room-photo"> {name.foto} </div>
                       <div className="room-join-button"> <button> Join  </button> </div>
                       </div>
                     )      
              })       


return (
             <div>
                     <div className = "rooms-section-title"> #Live now! 
                     <div className="room-new-button"> 
                         <button onClick={addroom}> Create Room </button> 
                     </div>
                     </div> 
                     <div className="rooms-container">
                     <div className="room-info"></div>
                     {roomslist}
                     </div>

                     </div>
       )
                     };

export default Corps;
