import React from "react";
import Corps from "./Corps";
import { useState, useContext } from 'react';
import { Context } from "./context";
import { FormControl, TextField, Button, TextareaAutosize, MenuItem, Modal } from '@material-ui/core';


const PopUp = ({ valeur }) => {

    const [roomtopic, setroomtopic] = useState('');
    const [description, setdescription] = useState('');
    const [currency, setCurrency] = React.useState('');
    const currencies = [
        {
            value: '1',
            label: '1',
        },
        {
            value: '2',
            label: '2',
        },
        {
            value: '3',
            label: '3',
        },
        {
            value: '4',
            label: '4',
        },
    ];


    const [rooms, setrooms] = useContext(Context);

    const handlechange = (e) => {
        setroomtopic(e.target.value);
    }
    const handlechange1 = (e) => {
        setdescription(e.target.value);
    }

    const Submit = (e) => {
        e.preventDefault();
        //Add to the previous rooms a new instance 
        setrooms(prevrooms => [...prevrooms, { name: roomtopic, foto: description }]);
        valeur()
    }

    return (


        <Modal
            open='true'
            onClose={valeur}
            aria-labelledby="simple-modal-title"
            aria-describedby="simple-modal-description"
        >

            <form>
                <TextField
                    id="standard-select-currency"
                    select
                    label="Participant Number"
                    value={currency}
                    helperText="Define the max number of participants"
                >
                    {currencies.map((option) => (
                        <MenuItem key={option.value} value={option.value}>
                            {option.label}
                        </MenuItem>
                    ))}
                </TextField>

                <TextField id="standard-helperText" label="Topic "
                    value={roomtopic}
                    onChange={handlechange}
                    color="secondary" />

                <br />   <br />

                <TextareaAutosize aria-label="minimum height"
                    rowsMin={5} placeholder="Description"
                    value={description}
                    onChange={handlechange1}
                />
                <br /><br />
                <Button type="submit" onClick={Submit}> SUBMIT </Button>
            </form>


        </Modal>


    )
};

export default PopUp;
