import React from "react";
import Corps from "./Corps";
import { useState } from 'react';
import { LocaleContext } from '../contextapi'


const PopUp = ({ valeur, submit }) => {

    const [userName, setUserName] = useState('');
    return (
        <div className="modal">
            <div className="modal_content">
                <div class="popupCloseButton" onClick={valeur}> &times;</div>
                <form>
                    <input
                        placeholder="username"
                        required="required"
                        value={userName}
                        onChange={(e) => setUserName(e.target.value)}
                    />
                    <button type="submit" onClick={submit(userName)}>   SUBMIT    </button>
                </form>
            </div>
        </div>
    )
};

export default PopUp;
