import React from "react";
import Corps from "./Corps";
import { useState, useContext } from 'react';
import { Context } from "./context";
import { FormControl } from '@material-ui/core';



const PopUp = ({ valeur }) => {

    const [roomtopic, setroomtopic] = useState('');
    const [roomfoto, setroomfoto] = useState('');

    const [rooms, setrooms] = useContext(Context);

    const handlechange = (e) => {
        setroomtopic(e.target.value);
    }
    const handlechange1 = (e) => {
        setroomfoto(e.target.value);
    }

    const Submit = (e) => {
        e.preventDefault();
        //Add to the previous rooms a new instance 
        setrooms(prevrooms => [...prevrooms, { name: roomtopic, foto: roomfoto }]);
        valeur()
    }

    return (
        <div className="modal">
            <div className="modal_content">
                <div className="popupCloseButton" onClick={valeur}> &times;</div>
                <form className={classes.root} noValidate autoComplete="off">
                    <Input defaultValue="Hello world" inputProps={{ 'aria-label': 'description' }} />
                    <Input placeholder="Placeholder" inputProps={{ 'aria-label': 'description' }} />
                    <Input defaultValue="Disabled" disabled inputProps={{ 'aria-label': 'description' }} />
                    <Input defaultValue="Error" error inputProps={{ 'aria-label': 'description' }} />
                </form>
            </div>
        </div>
    )
};

export default PopUp;
