import React from "react";
import Corps from "./Corps";
import { useState, useContext } from 'react';
import { Context } from "./context";
import { FormControl, TextField } from '@material-ui/core';


const PopUp = ({ valeur }) => {

    const [roomtopic, setroomtopic] = useState('');
    const [roomfoto, setroomfoto] = useState('');

    const [rooms, setrooms] = useContext(Context);

    const handlechange = (e) => {
        setroomtopic(e.target.value);
    }
    const handlechange1 = (e) => {
        setroomfoto(e.target.value);
    }

    const Submit = (e) => {
        e.preventDefault();
        //Add to the previous rooms a new instance 
        setrooms(prevrooms => [...prevrooms, { name: roomtopic, foto: roomfoto }]);
        valeur()
    }

    return (
        <div className="modal">
            <div className="modal_content">
                <div className="popupCloseButton" onClick={valeur}> &times;</div>
                <form>
                    <TextField
                        id="filled-secondary"
                        label="Filled secondary"
                        variant="filled"
                        color="secondary"
                        value={roomtopic}
                        onChange={handlechange}

                    />
                    <br /> <br />
                    <CssTextField
                        className={classes.margin}
                        label="Custom CSS"
                        variant="outlined"
                        id="custom-css-outlined-input"
                    />
                    <TextField
                        id="outlined-secondary"
                        label="Outlined secondary"
                        variant="outlined"
                        color="secondary"
                        value={roomfoto}
                        onChange={handlechange1}
                    />
                    <button type="submit" onClick={Submit}> SUBMIT </button>
                </form>
            </div>
        </div>
    )
};

export default PopUp;
